-- ----------------------------
-- Table structure for random_poem
-- ----------------------------
DROP TABLE IF EXISTS `random_poem`;
CREATE TABLE `random_poem`  (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT 'id',
  `excerpt` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '诗句内容',
  `poem_id` int(11) NULL DEFAULT NULL COMMENT '诗文id',
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 18 CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = Dynamic;

